CREATE OR REPLACE PROCEDURE html_desvinc_anexo_lote ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w	tiss_anexo_lote.ie_status%type;


BEGIN

begin
select 	ie_status
into STRICT	ie_status_w
from 	tiss_anexo_lote a,
	tiss_anexo_guia b
where 	a.nr_sequencia = b.nr_seq_lote_anexo
and	b.nr_sequencia	= nr_sequencia_p  LIMIT 1;
exception
when others then
	ie_status_w	:= '1';
end;

if (ie_status_w <> '1') then
	--O lote n√£o esta mais pendente!
	CALL wheb_mensagem_pck.exibir_mensagem_abort(334679);
else
	update	tiss_anexo_guia
	set	nr_seq_lote_anexo  = NULL,
		dt_inclusao_lote  = NULL,
		nm_usuario_incl_lote  = NULL
	where	nr_sequencia = nr_sequencia_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
