CREATE OR REPLACE PROCEDURE html_gravar_interrupcao_cir ( nr_seq_interrupcao_p bigint, nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE

						 
cd_estabelecimento_w	smallint;
cd_perfil_w		smallint;
cd_motivo_exc_conta_w	smallint;
						

BEGIN 
cd_estabelecimento_w 	:= wheb_usuario_pck.get_cd_estabelecimento;
cd_perfil_w		:= obter_perfil_ativo;	
 
cd_motivo_exc_conta_w := obter_param_usuario(872, 151, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, cd_motivo_exc_conta_w);
 
CALL gravar_interrupcao_cirurgia(nr_seq_interrupcao_p,nr_cirurgia_p,nm_usuario_p);
 
if (cd_motivo_exc_conta_w > 0) then 
	CALL excluir_mat_proc_cir(nr_cirurgia_p,cd_estabelecimento_w,cd_motivo_exc_conta_w,nm_usuario_p);
end if;	
 
end;
