CREATE OR REPLACE PROCEDURE html_liberar_prescricao ( nr_prescricao_p bigint, nm_usuario_p text, cd_perfil_p bigint, ds_aviso_p INOUT text) AS $body$
DECLARE

 
nr_atendimento_w	prescr_medica.nr_atendimento%type;						
ie_tipo_pessoa_w	pessoa_fisica.ie_tipo_pessoa%type;
ds_erro_w			varchar(2000);


BEGIN 
 
select	max(nr_atendimento) 
into STRICT	nr_atendimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
select	max(obter_tipo_pessoa(cd_pessoa_fisica)) 
into STRICT	ie_tipo_pessoa_w 
from	usuario 
where	nm_usuario = nm_usuario_p;
 
ds_erro_w := Liberar_prescricao(nr_prescricao_p, nr_atendimento_w, ie_tipo_pessoa_w, cd_perfil_p, nm_usuario_p, 'N', ds_erro_w);
 
ds_aviso_p := ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
