CREATE OR REPLACE PROCEDURE html_link_agenda_autor (NR_SEQ_AGENDA_P bigint, NR_SEQUENCIA_P bigint) AS $body$
DECLARE


ie_w	estagio_autorizacao.ie_autor_agenda%type;


BEGIN

CALL VINCULAR_AGENDA_CONV(NR_SEQ_AGENDA_P,NR_SEQUENCIA_P);

select	max(a.ie_autor_agenda) ie
into STRICT	ie_w
from	estagio_autorizacao a,
autorizacao_convenio b
where	a.nr_sequencia	= b.nr_seq_estagio
and	b.nr_sequencia	= NR_SEQUENCIA_P
