CREATE OR REPLACE PROCEDURE html_transfere_conta_excluido (nr_sequencia_p bigint, cd_convenio_p bigint, nm_usuario_p text, ie_proc_mat_p text) AS $body$
BEGIN
 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_proc_mat_p IS NOT NULL AND ie_proc_mat_p::text <> '') then 
 
	if (ie_proc_mat_p = 1) then 
		update	procedimento_paciente 
		set	nr_interno_conta  = NULL, 
			cd_motivo_exc_conta  = NULL, 
			ds_compl_motivo_excon  = NULL 
		where	nr_sequencia = nr_sequencia_p;
		 
		CALL atualiza_preco_procedimento(nr_sequencia_p,cd_convenio_p,nm_usuario_p);
		 
	else 
		update	material_atend_paciente 
		set	nr_interno_conta  = NULL, 
			cd_motivo_exc_conta  = NULL, 
			ds_compl_motivo_excon  = NULL 
		where	nr_sequencia = nr_sequencia_p;
		 
		CALL atualiza_preco_material(nr_sequencia_p,nm_usuario_p);
		 
	end if;	
end if;
 
commit;
 
end;
$body$
