CREATE OR REPLACE PROCEDURE html_update_function_param ( cd_funcao_p bigint, nr_seq_param_p bigint, ie_situacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (coalesce(nr_seq_param_p,0) > 0) and (ie_situacao_p in ('A','I')) then

	update	funcao_parametro
	set	ie_situacao_html5 = ie_situacao_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	cd_funcao 	= cd_funcao_p
	and	nr_sequencia	= nr_seq_param_p;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
