CREATE OR REPLACE PROCEDURE ia_compl_pf_cadsus ( cd_pessoa_fisica_p text, nm_contato_p text, ie_tipo_complemento_p bigint, /*4 - Pai; 5 - Mae*/
 nm_usuario_p text ) AS $body$
BEGIN

    MERGE INTO compl_pessoa_fisica a USING( SELECT
        nm_contato_p nm_contato,
        cd_pessoa_fisica_p cd_pessoa_fisica,
        ie_tipo_complemento_p ie_tipo_complemento

    )
    b ON (
        a.ie_tipo_complemento = b.ie_tipo_complemento
        AND a.cd_pessoa_fisica = b.cd_pessoa_fisica
    )
    WHEN MATCHED THEN UPDATE SET a.nm_contato = b.nm_contato
    WHEN NOT MATCHED THEN INSERT(
        a.nr_sequencia,
        a.ie_tipo_complemento,
        a.cd_pessoa_fisica,
        a.nm_contato,
        a.dt_atualizacao,
        a.nm_usuario,
        a.dt_atualizacao_nrec,
        a.nm_usuario_nrec
    ) VALUES (
        (
            SELECT
                coalesce(MAX(nr_sequencia),0) + 1
            FROM
                compl_pessoa_fisica
            WHERE
                cd_pessoa_fisica = cd_pessoa_fisica_p
        ),
        b.ie_tipo_complemento,
        b.cd_pessoa_fisica,
        b.nm_contato,
        clock_timestamp(),
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_p
    );

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
