CREATE OR REPLACE PROCEDURE importar_alt_valor_tit_rec ( cd_moeda_p bigint, cd_motivo_p bigint, ds_observacao_p text, dt_alteracao_p timestamp, dt_atualizacao_p timestamp, ie_aumenta_diminui_p text, nm_usuario_p text, nr_seq_trans_fin_p bigint, nr_titulo_externo_p text, vl_alteracao_p bigint) AS $body$
DECLARE

/* 
 
ie_aumenta_diminui			A - Aumenta o Valor 
					D - Diminui o Valor 
 
cd_motivo				1 - Desconto Concedido 
					2 - Aumento de Valor 
 
*/
 
 
nr_sequencia_w		bigint;
vl_anterior_w		double precision;
nr_titulo_w		bigint;


BEGIN 
 
select	max(nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_receber 
where	nr_titulo_externo = nr_titulo_externo_p;
 
if (coalesce(nr_titulo_w::text, '') = '') then 
	--r.aise_application_error(-20011,'Título não encontrado'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267406);
 
else 
	select 	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_sequencia_w 
	from	alteracao_valor 
	where	nr_titulo = nr_titulo_w;
	 
	select	vl_saldo_titulo 
	into STRICT	vl_anterior_w 
	from	titulo_receber 
	where	nr_titulo = nr_titulo_w;
end if;
 
insert 	into 	alteracao_valor(nr_sequencia, 
				cd_moeda, 
				cd_motivo, 
				ds_observacao, 
				dt_alteracao, 
				dt_atualizacao, 
				ie_aumenta_diminui, 
				nm_usuario, 
				nr_seq_trans_fin, 
				nr_titulo, 
				vl_alteracao, 
				vl_anterior) 
		values (nr_sequencia_w, 
				cd_moeda_p, 
				cd_motivo_p, 
				ds_observacao_p, 
				dt_alteracao_p, 
				dt_atualizacao_p, 
				ie_aumenta_diminui_p, 
				nm_usuario_p, 
				nr_seq_trans_fin_p, 
				nr_titulo_w, 
				vl_alteracao_p, 
				vl_anterior_w);
		 
CALL atualizar_saldo_tit_rec(nr_titulo_w,nm_usuario_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
