CREATE OR REPLACE PROCEDURE importar_arquivo_301 ( ds_conteudo_p text, nr_seq_log_scheduler_p bigint, ds_nm_arquivo_p text, dt_arquivo_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_lote_w			d301_lote_retorno.nr_sequencia%type;
nr_seq_arquivo_w		d301_arquivo_retorno.nr_sequencia%type;
nr_seq_arquivo_conteudo_w	d301_arquivo_conteudo.nr_sequencia%type;
qt_w				bigint;

BEGIN

qt_w := 0;

--controla pelo nome do arquivo, já que o nome do arquivo é um sequêncial
begin
select	count(*)
into STRICT	qt_w
from	d301_arquivo_retorno
where	ds_nome_arquivo = ds_nm_arquivo_p  LIMIT 1;
exception
when others then
	qt_w := 0;
end;

if (qt_w = 0) and (ds_conteudo_p IS NOT NULL AND ds_conteudo_p::text <> '') then
	select	nextval('d301_lote_retorno_seq')
	into STRICT	nr_seq_lote_w
	;

	insert into d301_lote_retorno(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		dt_lote,
		dt_importacao,
		nr_seq_log_scheduler)
	values (nr_seq_lote_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		dt_arquivo_p,
		clock_timestamp(),
		nr_seq_log_scheduler_p);

	select	nextval('d301_arquivo_retorno_seq')
	into STRICT	nr_seq_arquivo_w
	;

	insert into d301_arquivo_retorno(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_lote_retorno,
		ds_nome_arquivo,
		dt_geracao)
	values (nr_seq_arquivo_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_lote_w,
		ds_nm_arquivo_p,
		clock_timestamp());

	select	nextval('d301_arquivo_conteudo_seq')
	into STRICT	nr_seq_arquivo_conteudo_w
	;

	insert into	d301_arquivo_conteudo(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_arquivo_retorno,
		ds_conteudo)
	values (nr_seq_arquivo_conteudo_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_arquivo_w,
		ds_conteudo_p);

	commit;

	CALL importar_d301_datasets(nr_seq_arquivo_conteudo_w,nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
