CREATE OR REPLACE PROCEDURE importar_baixa_tit_pag_arq (nr_titulo_p bigint, dt_baixa_p timestamp, cd_moeda_p bigint, cd_tipo_baixa_p bigint, vl_baixa_p bigint, vl_descontos_p bigint, vl_outras_deducoes_p bigint, vl_juros_p bigint, vl_multa_p bigint, vl_outros_acrescimos_p bigint, vl_pago_p bigint, nr_seq_trans_fin_p bigint, nr_seq_conta_banco_p bigint, cd_conta_contabil_p text, cd_centro_custo_p bigint, ds_observacao_p text, nm_usuario_p text, nr_externo_p text) AS $body$
DECLARE

 
 
nr_sequencia_w			integer;
qt_externo_w			bigint;

 
 
/* 
Procedure utilizada para importação padrão WHEB das baixas de títulos a pagar 
Usar interface 1619 - "Integração WHEB - Importar Baixas títulos a pagar 1.0" 
*/
 
 

BEGIN 
 
if (coalesce(nr_titulo_p::text, '') = '') then 
	--r.aise_application_error(-20011,'Título não encontrado'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267406);
else 
	select 	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_sequencia_w 
	from	titulo_pagar_baixa 
	where	nr_titulo = nr_titulo_p;
end if;
 
select	count(*) 
into STRICT	qt_externo_w 
from	titulo_pagar_baixa 
where	nr_titulo	= nr_titulo_p 
and	nr_externo	= nr_externo_p;
 
if (qt_externo_w = 0) then 
	begin 
	insert	into titulo_pagar_baixa(nr_titulo, 
					nr_sequencia, 
					dt_atualizacao, 
					nm_usuario, 
					dt_baixa, 
					cd_moeda, 
					cd_tipo_baixa, 
					ie_acao, 
					vl_baixa, 
					vl_descontos, 
					vl_outras_deducoes, 
					vl_juros, 
					vl_multa, 
					vl_outros_acrescimos, 
					vl_pago, 
					vl_devolucao, 
					nr_seq_trans_fin, 
					nr_seq_conta_banco, 
					cd_conta_contabil, 
					cd_centro_custo, 
					ds_observacao, 
					nr_externo) 
				values (nr_titulo_p, 
					nr_sequencia_w, 
					clock_timestamp(), 
					nm_usuario_p, 
					dt_baixa_p, 
					1, 
					cd_tipo_baixa_p, 
					'I', 
					vl_baixa_p, 
					vl_descontos_p, 
					vl_outras_deducoes_p, 
					vl_juros_p, 
					vl_multa_p, 
					vl_outros_acrescimos_p, 
					vl_pago_p, 
					0, 
					null, 
					null, 
					null, 
					null, 
					ds_observacao_p, 
					nr_externo_p);
 
	CALL atualizar_saldo_tit_pagar(nr_titulo_p,nm_usuario_p);
	CALL Gerar_W_Tit_Pag_imposto(nr_titulo_p, nm_usuario_p);
 
	commit;
	end;
 
/*else 
 
	insert into logxxx_tasy 
		(cd_log, 
		nm_usuario, 
		dt_atualizacao, 
		ds_log) 
	values	(55784, 
		'IMP_WHEB', 
		sysdate, 
		'Erro ao inserir o título ' || nr_titulo_p || chr(13) || chr(10) || 
		'Erro: Esta baixa já foi importada!');*/
 
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
