SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE importar_banco_escrit_barras ( nm_usuario_p text, nr_seq_conta_banco_p bigint, cd_banco_p bigint, nr_seq_banco_escrit_p bigint, ds_conteudo_p text, nr_seq_lote_p bigint, ds_arquivo_p text) AS $body$
DECLARE


ie_forma_importacao_barras_w 	 	varchar(2);
cd_banco_w 				banco_estabelecimento.cd_banco%type;
nm_obj_barras_w				banco.nm_obj_barras%type;


BEGIN

ie_forma_importacao_barras_w := coalesce(subStr(OBTER_PARAMETRO_USUARIO_JS(857,32), 1 , 2 ), '1');

if (ie_forma_importacao_barras_w = '2') then
	select	max(a.cd_banco)
	into STRICT	cd_banco_w
	from	banco_estabelecimento a
	where	a.nr_sequencia = nr_seq_conta_banco_p;
else
	cd_banco_w := cd_banco_p;
end if;

select	max(nm_obj_barras)
into STRICT 	nm_obj_barras_w
from	banco a
where	a.cd_banco  = cd_banco_p;

if (nm_obj_barras_w = '') then
	if (ie_forma_importacao_barras_w <> '2') then

		insert  into banco_escrit_barras(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_banco_escrit,
			cd_barras)
		values (nextval('banco_escrit_barras_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_banco_escrit_p,
			ds_conteudo_p);

	else

		insert  into banco_escrit_barras(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_lote,
			cd_barras)
		values (nextval('banco_escrit_barras_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_lote_p,
			ds_conteudo_p);
	end if;

else

	insert   into w_barras(ds_conteudo,
	       dt_atualizacao,
	       nm_usuario,
	       nr_sequencia)
	values (ds_conteudo_p,
	       clock_timestamp(),
	       nm_usuario_p,
	       nextval('w_barras_seq'));

end if;

if (ie_forma_importacao_barras_w = '2') then

	update   banco_escrit_lote
	set      ds_arquivo     = ds_arquivo_p
	where    nr_sequencia   = nr_seq_lote_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
