CREATE OR REPLACE PROCEDURE importar_canc_tit_rec_arq (nr_titulo_p text, nm_usuario_p text, nr_externo_p text) AS $body$
DECLARE

 
qt_externo_w		bigint;
nr_seq_alt_valor_w		bigint;

/* 
Procedure utilizada para importação padrão WHEB dos cancelamentos de títulos a receber 
Usar interface 1623 - "Integração WHEB - Importar Cancelamento títulos a receber 1.0" 
*/
 
 

BEGIN 
 
IF (coalesce(nr_titulo_p::text, '') = '') THEN 
	--R.AISE_APPLICATION_ERROR(-20011,'Título não encontrado'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267406);
end if;
 
select	count(*) 
into STRICT	qt_externo_w 
from	alteracao_valor 
where	nr_titulo	= nr_titulo_p 
and	nr_externo	= nr_externo_p;
 
if (qt_externo_w = 0) then 
	begin 
	CALL Cancelar_Titulo_Receber(nr_titulo_p,nm_usuario_p,'S',clock_timestamp());
 
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_alt_valor_w 
	from	alteracao_valor 
	where	nr_titulo = nr_titulo_p;
	 
	update	alteracao_valor 
	set	nr_externo 	= nr_externo_p 
	where	nr_titulo 	= nr_titulo_p 
	and	nr_sequencia	= nr_seq_alt_valor_w;
	end;
 
 
/*else 
 
	insert into logxxx_tasy 
		(cd_log, 
		nm_usuario, 
		dt_atualizacao, 
		ds_log) 
	values	(55787, 
		'IMP_WHEB', 
		sysdate, 
		'Erro ao inserir o título ' || nr_titulo_p || chr(13) || chr(10) || 
		'Erro: Este cancelamento já foi importado!');*/
 
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
