CREATE OR REPLACE PROCEDURE importar_d301_segmento_txt (nr_seq_dataset_p bigint, ds_conteudo_txt_p text, nm_usuario_p text) AS $body$
DECLARE


 ds_conteudo_txt_w    varchar(4000);


BEGIN
  if (substr(ds_conteudo_txt_p,1,3) = 'TXT') then

        ds_conteudo_txt_w:=replace(ds_conteudo_txt_p,'''','');

        insert into d301_segmento_txt(nr_sequencia,
            dt_atualizacao,
            nm_usuario,
            dt_atualizacao_nrec,
            nm_usuario_nrec,
            ds_justificativa_med,
            nr_seq_dataset,
            nr_seq_dataset_ret)
       values (nextval('d301_segmento_txt_seq'),
              clock_timestamp(),
              nm_usuario_p,
              clock_timestamp(),
              nm_usuario_p,
              obter_valor_separador(ds_conteudo_txt_w,2,'+'),
              null,
              nr_seq_dataset_p);
    commit;
  end if;
end;
$body$
