SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE importar_d301_segmento_unh ( nr_seq_dataset_p bigint, ds_conteudo_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estrut_arq_w	c301_estrutura_arq.nr_sequencia%type;


BEGIN

if (substr(ds_conteudo_p,1,3) = 'UNH') then

	insert into D301_SEGMENTO_UNH(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_ref_segmento,
		ie_dataset,
		ie_versao_dataset,
		nr_release_dataset,
		nr_versao_organiz,
		nr_seq_dataset,
		nr_seq_dataset_ret)
	values (nextval('d301_segmento_unh_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		obter_valor_campo_separador(ds_conteudo_p,2,'+'),
		substr(obter_valor_campo_separador(ds_conteudo_p,3,'+'),1,4),
		obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),2,':'),
		obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),3,':'),
		obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),4,':'),
		null,
		nr_seq_dataset_p);

	select	max(nr_sequencia)
	into STRICT	nr_seq_estrut_arq_w
	from	c301_estrutura_arq a
	where	a.ie_versao		= obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),2,':')
	and	a.nr_release		= obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),3,':')
	and	a.nr_versao_organiz	= obter_valor_campo_separador(obter_valor_campo_separador(ds_conteudo_p,3,'+'),4,':')
	and	exists (SELECT	1
		from	c301_estrutura_seg_dataset x
		where	x.nr_seq_estrut_arq	= a.nr_sequencia
		and	x.ie_dataset		= substr(obter_valor_campo_separador(ds_conteudo_p,3,'+'),1,4));

	update	d301_arquivo_retorno a
	set	a.nr_seq_estrut_arq	= nr_seq_estrut_arq_w
	where	coalesce(a.nr_seq_estrut_arq::text, '') = ''
	and	a.nr_sequencia =
		(SELECT	max(x.nr_seq_arquivo)
		from	d301_dataset_retorno x
		where	x.nr_sequencia	= nr_seq_dataset_p);

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
