CREATE OR REPLACE PROCEDURE importar_d301_segmento_zpr (nr_seq_dataset_p bigint, ds_conteudo_zpr_p text, nm_usuario_p text) AS $body$
DECLARE

 ds_conteudo_zpr_w    varchar(4000);

BEGIN

  if (substr(ds_conteudo_zpr_p,1,3) = 'ZPR') then

        ds_conteudo_zpr_w:=replace(ds_conteudo_zpr_p,'''','');

        insert into d301_segmento_zpr(nr_sequencia,
            dt_atualizacao,
            nm_usuario,
            dt_atualizacao_nrec,
            nm_usuario_nrec,
            vl_fatura,
            nr_seq_301_resp_audit,
            vl_taxa_recalc,
            vl_pacote_recalc,
            nr_seq_dataset,
            nr_seq_dataset_ret)
       values (nextval('d301_segmento_zpr_seq'),
              clock_timestamp(),
              nm_usuario_p,
              clock_timestamp(),
              nm_usuario_p,
              obter_valor_separador(ds_conteudo_zpr_w,2,'+'),
              obter_seq_valor_301('C301_10_RESPOSTA_AUDIT','IE_RESPOSTA',obter_valor_separador(ds_conteudo_zpr_w,3,'+')),
              obter_valor_separador(ds_conteudo_zpr_w,4,'+'),
              obter_valor_separador(ds_conteudo_zpr_w,5,'+'),
              null,
              nr_seq_dataset_p);
     commit;
  end if;


end;
