CREATE OR REPLACE PROCEDURE importar_de_para_tuss (cd_estabelecimento_p bigint, cd_material_p bigint, cd_material_tuss_p bigint, dt_inicio_vigencia_p timestamp, nr_seq_tuss_mat_p bigint, cd_convenio_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_tuss_mat_item_w			material_tuss.nr_seq_tuss_mat_item%type;
ie_existe_material_w			varchar(1) := 'N';


BEGIN


select	coalesce(max(b.nr_sequencia),0)
into STRICT	nr_seq_tuss_mat_item_w
from	tuss_material_item b,
	tuss_material a
where	a.nr_sequencia 		= b.nr_seq_carga_tuss
and	a.nr_sequencia 		= nr_seq_tuss_mat_p
and	b.cd_material_tuss	= cd_material_tuss_p;



begin
select	'S'
into STRICT	ie_existe_material_w
from	material
where	cd_material = cd_material_p  LIMIT 1;
exception
when others then
	ie_existe_material_w := 'N';
end;

if (nr_seq_tuss_mat_item_w > 0) and (coalesce(ie_existe_material_w,'N') = 'S') then

	insert into material_tuss(
		cd_estabelecimento,
		cd_material,
		cd_material_tuss,
		dt_atualizacao,
		dt_atualizacao_nrec,
		dt_vigencia_inicial,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_tuss_mat,
		nr_seq_tuss_mat_item,
		nr_sequencia,
		cd_convenio)
	values (cd_estabelecimento_p,
		cd_material_p,
		cd_material_tuss_p,
		clock_timestamp(),
		clock_timestamp(),
		dt_inicio_vigencia_p,
		'A',
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_tuss_mat_p,
		nr_seq_tuss_mat_item_w,
		nextval('material_tuss_seq'),
		cd_convenio_p);

end if;


commit;

end;
