CREATE OR REPLACE PROCEDURE importar_ops_no_case ( nr_sequencia_p procedimento_pac_medico.nr_sequencia%TYPE, ie_tipo_p text, -- E - episodio; A - atendimento
 nm_usuario_p text, nr_seq_episodio_p procedimento_pac_medico.nr_seq_episodio%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type) AS $body$
  -- nr_seq_episodio ou nr_atendimento
DECLARE



procedimento_pac_medico_w	procedimento_pac_medico%rowtype;


BEGIN

begin
select	*
into STRICT	procedimento_pac_medico_w
FROM    procedimento_pac_medico a
WHERE   a.nr_sequencia  = nr_sequencia_p;
exception
when others then
	procedimento_pac_medico_w.nr_sequencia := null;
end;

if (procedimento_pac_medico_w.nr_sequencia IS NOT NULL AND procedimento_pac_medico_w.nr_sequencia::text <> '') then


	select  nextval('procedimento_pac_medico_seq')
	into STRICT    procedimento_pac_medico_w.nr_sequencia
	;
	
	procedimento_pac_medico_w.dt_atualizacao	:= clock_timestamp();
	procedimento_pac_medico_w.dt_atualizacao_nrec	:= clock_timestamp();
	procedimento_pac_medico_w.dt_procedimento	:= clock_timestamp();
	procedimento_pac_medico_w.nm_usuario		:= nm_usuario_p;
	procedimento_pac_medico_w.nm_usuario_nrec	:= nm_usuario_p;
	procedimento_pac_medico_w.dt_liberacao		:= null;	
	
	if (ie_tipo_p = 'E') then	
		procedimento_pac_medico_w.nr_seq_episodio	:= nr_seq_episodio_p;
		procedimento_pac_medico_w.nr_atendimento	:= nr_atendimento_p;
	elsif (ie_tipo_p = 'A') then
		procedimento_pac_medico_w.nr_seq_episodio	:= null;
		procedimento_pac_medico_w.nr_atendimento	:= nr_atendimento_p;
	end if;
	
	insert into procedimento_pac_medico values (procedimento_pac_medico_w.*);
	
	commit;
	
end if;

end;
$body$
