SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE importar_urs_por_funcao ( cd_funcao_p bigint, nr_seq_reg_program_p bigint, nm_usuario_p text, nr_seq_intencao_uso_p bigint default 2) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT distinct nr_customer_requirement
	from 	reg_product_requirement	
	where 	coalesce(ie_situacao, 'A') = 'A'
    and     (dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')
    and     nr_seq_intencao_uso = coalesce(nr_seq_intencao_uso_p, 2)
	and		nr_sequencia in (	SELECT nr_seq_product_req
				from reg_funcao_pr
				where cd_funcao = cd_funcao_p	);

nr_seq_program_urs_w 	reg_program_urs.nr_sequencia%type;

BEGIN

for C01_w in C01 loop
begin

	select 	max(nr_sequencia)
	into STRICT 	nr_seq_program_urs_w
	from 	reg_program_urs
	where 	nr_seq_reg_program	= nr_seq_reg_program_p
	and 	nr_customer_requirement 	= C01_w.nr_customer_requirement;

	if (coalesce(nr_seq_program_urs_w::text, '') = '') then

		insert into reg_program_urs(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_situacao,
			nr_seq_reg_program,
			nr_customer_requirement,
			ds_justificativa_inativacao
		) values (
			nextval('reg_program_urs_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'A',
			nr_seq_reg_program_p,
			C01_w.nr_customer_requirement,
			null
		);

		commit;

		CALL add_reg_program_prs(	nr_seq_reg_program_p,
					C01_w.nr_customer_requirement,
					nm_usuario_p	);

	end if;

exception when others then rollback;

end;
end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
