CREATE OR REPLACE PROCEDURE importa_cep_bairro ( nr_sequencia_p bigint, ds_bairro_p text, nr_seq_loc_p bigint, ds_uf_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	integer:= 0;


BEGIN

if (obter_se_uf_valido(ds_uf_p)) then
	select	count(*)
	into STRICT	qt_existe_w
	from	cep_bairro
	where	nr_sequencia	= nr_sequencia_p
	and	nr_seq_loc	= nr_seq_loc_p;

	if (qt_existe_w > 0) then
		/*(-20011,	'JÃ¡ existe um registro com esses dados. ' || chr(10) ||
						'Seq: ' || nr_sequencia_p	|| '; ' ||
						'Loc: ' || nr_seq_loc_p);*/
		CALL wheb_mensagem_pck.exibir_mensagem_abort(263188,'NR_SEQUENCIA=' || nr_sequencia_p || ';NR_SEQ_LOC=' || nr_seq_loc_p);
	else
		insert into cep_bairro(
				nr_sequencia,
				ds_bairro,
				nr_seq_loc,
				ds_uf,
				dt_atualizacao,
				nm_usuario)
			values (nr_sequencia_p,
				ds_bairro_p,
				nr_seq_loc_p,
				ds_uf_p,
				clock_timestamp(),
				nm_usuario_p);
	end if;
else
	insert into	log_mov( dt_atualizacao
						, nm_usuario
						, ds_log
						, cd_log)
				values ( clock_timestamp()
						, obter_usuario_Ativo
						, 'nr_sequencia_p:' || nr_sequencia_p || chr(13) ||
						  'ds_bairro_p:' || ds_bairro_p || chr(13) ||
						  'nr_seq_loc_p:' || nr_seq_loc_p || chr(13) ||
						  'ds_uf_p:' || ds_uf_p || chr(13) ||
						  'nm_usuario_p:' || nm_usuario_p
						, 1574783);
end if;

commit;
end	;
$body$
