CREATE OR REPLACE PROCEDURE importa_contrato_equip ( nr_seq_equipamento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_cgc_contratado_w	varchar(14);
dt_fim_w		timestamp;
dt_inicio_w		timestamp;
vl_total_contrato_w	bigint;
nr_seq_tipo_contrato_w	bigint;
nr_seq_contrato_w	bigint;
contador_w		bigint;

C01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_cgc_contratado,
	a.dt_fim,
	a.dt_inicio,
	coalesce(a.vl_total_contrato,0),
	a.nr_seq_tipo_contrato
from	contrato a,
	contrato_regra_nf b
where	a.nr_sequencia = b.nr_seq_contrato
and	b.nr_seq_equipamento = nr_seq_equipamento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_contrato_w,
	cd_cgc_contratado_w,
	dt_fim_w,
	dt_inicio_w,
	vl_total_contrato_w,
	nr_seq_tipo_contrato_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	contador_w
	from	man_contrato_manut
	where	nr_seq_contrato = nr_seq_contrato_w
	and	nr_seq_equipamento = nr_seq_equipamento_p;

	if (coalesce(contador_w,0) = 0) then

		insert into MAN_CONTRATO_MANUT(nr_sequencia,
						nr_seq_equipamento,
						dt_atualizacao,
						nm_usuario,
						dt_inicio_contrato,
						dt_fim_contrato,
						nr_documento,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_cnpj,
						nr_seq_tipo_contrato,
						vl_contrato,
						nr_seq_contrato,
						ie_periodo_valor
						)
					values (
						nextval('man_contrato_manut_seq'),
						nr_seq_equipamento_p,
						clock_timestamp(),
						nm_usuario_p,
						dt_inicio_w,
						dt_fim_w,
						null,
						clock_timestamp(),
						nm_usuario_p,
						cd_cgc_contratado_w,
						null,
						vl_total_contrato_w,
						nr_seq_contrato_w,
						null
						);
	end if;

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
