CREATE OR REPLACE PROCEDURE importa_valor_rateio_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_centro_custo_w		bigint;
cd_conta_contabil_w		varchar(20);
cd_conta_financ_w		bigint;
vl_rateio_w			double precision;
vl_frete_w			double precision;
vl_desconto_w			double precision;
vl_seguro_w			double precision;
vl_despesa_acessoria_w		double precision;

c01 CURSOR FOR
SELECT	cd_centro_custo,
	cd_conta_contabil,
	cd_conta_financ,
	coalesce(vl_rateio,0),
	coalesce(vl_frete,0),
	coalesce(vl_desconto,0),
	coalesce(vl_seguro,0),
	coalesce(vl_despesa_acessoria,0)
from	w_importa_rateio_solic
where	nr_solic_compra = nr_solic_compra_p
and	nm_usuario = nm_usuario_p
and	(vl_rateio IS NOT NULL AND vl_rateio::text <> '')
and	vl_rateio > 0
and	((cd_centro_custo IS NOT NULL AND cd_centro_custo::text <> '') or (cd_conta_contabil IS NOT NULL AND cd_conta_contabil::text <> '') or (cd_conta_financ IS NOT NULL AND cd_conta_financ::text <> ''));


BEGIN

open c01;
loop
fetch c01 into
	cd_centro_custo_w,
	cd_conta_contabil_w,
	cd_conta_financ_w,
	vl_rateio_w,
	vl_frete_w,
	vl_desconto_w,
	vl_seguro_w,
	vl_despesa_acessoria_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	insert into solic_compra_item_rateio(
		nr_sequencia,
		nr_solic_compra,
		nr_item_solic_compra,
		dt_atualizacao,
		nm_usuario,
		cd_centro_custo,
		cd_conta_contabil,
		cd_conta_financ,
		vl_rateio,
		vl_frete,
		vl_desconto,
		vl_seguro,
		vl_despesa_acessoria,
		ie_situacao)
	values ( nextval('solic_compra_item_rateio_seq'),
		nr_solic_compra_p,
		nr_item_solic_compra_p,
		clock_timestamp(),
		nm_usuario_p,
		CASE WHEN cd_centro_custo_w=0 THEN null  ELSE cd_centro_custo_w END ,
		cd_conta_contabil_w,
		CASE WHEN cd_conta_financ_w=0 THEN null  ELSE cd_conta_financ_w END ,
		vl_rateio_w,
		vl_frete_w,
		vl_desconto_w,
		vl_seguro_w,
		vl_despesa_acessoria_w,
		'A');

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
