CREATE OR REPLACE PROCEDURE import_apac_cid_topo (ds_doenca_cid_p text, cd_topografia_p text, cd_categoria_p text, ie_restricao_sexo_p text, ie_situacao_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: insere dado na tabela cido_topografia
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao
UP - updade
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (upper(ie_opcao_p) = 'UP') and (ds_doenca_cid_p IS NOT NULL AND ds_doenca_cid_p::text <> '') and (cd_topografia_p IS NOT NULL AND cd_topografia_p::text <> '') and (ie_situacao_p IS NOT NULL AND ie_situacao_p::text <> '') then
		update	cido_topografia
		set	ds_topografia = ds_doenca_cid_p,
			dt_atualizacao    = clock_timestamp(),
			nm_usuario        = nm_usuario_p,
			cd_categoria      = substr(cd_topografia_p,1,3),
			ie_sexo           = ie_restricao_sexo_p,
			ie_situacao       = ie_situacao_p
		where	cd_topografia     = cd_topografia_p;
		commit;

elsif (upper(ie_opcao_p) = 'IN') and (ds_doenca_cid_p IS NOT NULL AND ds_doenca_cid_p::text <> '') and (cd_topografia_p IS NOT NULL AND cd_topografia_p::text <> '') and (ie_situacao_p IS NOT NULL AND ie_situacao_p::text <> '') and (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') then
			insert	into cido_topografia(cd_topografia,
					ds_topografia,
					dt_atualizacao,
					nm_usuario,
					cd_categoria,
					ie_sexo,
					ie_situacao)
			values (cd_topografia_p,
					ds_doenca_cid_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_categoria_p,
					ie_restricao_sexo_p,
					ie_situacao_p);
			commit;
end if;
end;
