CREATE OR REPLACE PROCEDURE import_apac_cid (ds_doenca_cid_p text, cd_doenca_cid_p text, cd_categoria_cid_p text, ie_restricao_sexo_p text, qt_campos_radio_p text, ie_estadio_p text, ie_subcat_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: insere dado na tabela cid_doenca
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao
AT - atualizacao inicial
UP - updade
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (upper(ie_opcao_p) = 'AT') then
		update	cid_doenca
		set	ie_situacao = 'I',
			nm_usuario = 'Tasy',
			dt_atualizacao = clock_timestamp()
		where	ie_situacao = 'A';
		commit;

elsif (upper(ie_opcao_p) = 'UP') and (ds_doenca_cid_p IS NOT NULL AND ds_doenca_cid_p::text <> '') and (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') then
			update	cid_doenca
			set	ds_doenca_cid     = ds_doenca_cid_p,
				dt_atualizacao    = clock_timestamp(),
				nm_usuario        = nm_usuario_p,
				cd_categoria_cid  = substr(cd_doenca_cid_p,1,3),
				ie_sexo           = ie_restricao_sexo_p,
				ie_cad_interno    = 'N',
				ie_situacao       = 'A',
				qt_campos_radio   = qt_campos_radio_p,
				ie_Estadio        = ie_estadio_p,
				ie_subcategoria   = ie_subcat_p
			where	cd_doenca_cid     = cd_doenca_cid_p;
			commit;

elsif (upper(ie_opcao_p) = 'IN') and (ds_doenca_cid_p IS NOT NULL AND ds_doenca_cid_p::text <> '') and (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') and (cd_categoria_cid_p IS NOT NULL AND cd_categoria_cid_p::text <> '') then
			insert	into cid_doenca(cd_doenca_cid,
					ds_doenca_cid,
					dt_atualizacao,
					nm_usuario,
					cd_categoria_cid,
					ie_sexo,
					ie_cad_interno,
					ie_situacao,
					qt_campos_radio,
					ie_estadio,
					ie_subcategoria)
			values (cd_doenca_cid_p,
					ds_doenca_cid_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_categoria_cid_p,
					ie_restricao_sexo_p,
					'N',
					'A',
					qt_campos_radio_p,
					ie_estadio_p,
					ie_subcat_p);

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
