SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE import_dep_ident_bradesco_dp06 ( nr_seq_lote_dep_ident_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_deposito_w			timestamp;
ds_dt_deposito_w		varchar(15);
cd_agencia_recebedora_w		varchar(6);
ie_digito_agencia_w		varchar(1);
cd_identificacao_w		varchar(25);
vl_deposito_especie_w		double precision;
vl_deposito_cheque_w		double precision;
vl_deposito_w			double precision;
nr_seq_lote_w			bigint;
ie_forma_deposito_w		smallint;
nr_sequencia_w			bigint;
dt_geracao_arquivo_w		timestamp;
ds_dt_geracao_arquivo_w		varchar(15);
cd_agencia_header_w		varchar(4);
cd_conta_header_w		varchar(10);
cd_banco_lote_w			smallint;
cd_agencia_lote_w		varchar(10);
cd_conta_lote_w			varchar(10);
ie_digito_conta_header_w	varchar(1);
ie_digito_conta_lote_w		varchar(1);

ds_vl_1_w			varchar(18);
ds_vl_2_w			varchar(18);
ds_vl_3_w			varchar(18);

c01 CURSOR FOR
	SELECT	substr(ds_string,2,8) || ' ' || substr(ds_string,15,6) ds_dt_deposito,
		substr(ds_string,10,4) cd_agencia_recebedora,
		substr(ds_string,14,1) ie_digito_agencia,
		(substr(ds_string,43,18))::numeric  cd_identificacao,
		substr(ds_string,61,13) || ',' || substr(ds_string,74,2) vl_deposito_especie,
		substr(ds_string,76,13) || ',' || substr(ds_string,89,2) vl_deposito_cheque,
		substr(ds_string,91,13) || ',' || substr(ds_string,104,2) vl_deposito,
		obter_somente_numero(substr(ds_string,113,2)) ie_forma_deposito
	from	w_retorno_banco
	where	nr_seq_lote_dep_ident = nr_seq_lote_dep_ident_p
	and	substr(ds_string,1,1)	= '1'
	and	nm_usuario = nm_usuario_p;


BEGIN

delete	FROM lote_ret_dep_ident_item
where	nr_seq_lote = nr_seq_lote_dep_ident_p;

/* Obter informações do Header */

select	substr(ds_string,124,4) cd_agencia,
	substr(ds_string,150,8) || ' ' || substr(ds_string,158,6) ds_dt_geracao_arquivo,
	substr(ds_string,133,10) cd_conta,
	substr(ds_string,143,1) ie_digito_conta
into STRICT	cd_agencia_header_w,
	ds_dt_geracao_arquivo_w,
	cd_conta_header_w,
	ie_digito_conta_header_w
from	w_retorno_banco a
where	a.nr_seq_lote_dep_ident	= nr_seq_lote_dep_ident_p
and	substr(ds_string,1,1) = '0';

if (length(ds_dt_geracao_arquivo_w) = 15) then
	dt_geracao_arquivo_w	:= to_date(ds_dt_geracao_arquivo_w,'DDMMYYYY HH24MISS');
end if;

/* Obter dados bancários do lote */

select	max(b.cd_banco),
	max(b.cd_agencia_bancaria),
	max(b.cd_conta),
	max(b.ie_digito_conta)
into STRICT	cd_banco_lote_w,
	cd_agencia_lote_w,
	cd_conta_lote_w,
	ie_digito_conta_lote_w
from	banco_estabelecimento b,
	lote_ret_deposito_ident a
where	a.nr_seq_conta_banco	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_lote_dep_ident_p;

/* Consistências */

if (cd_banco_lote_w <> 237) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280450);
elsif (somente_numero(cd_agencia_header_w) <> somente_numero(cd_agencia_lote_w)) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280451, 'CD_AGENCIA_HEADER_P=' || cd_agencia_header_w);
elsif (somente_numero(cd_conta_header_w) <> somente_numero(cd_conta_lote_w)) or (somente_numero(ie_digito_conta_header_w) <> somente_numero(ie_digito_conta_lote_w)) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280452, 'IE_DIGITO_CONTA_HEADER_P=' || ie_digito_conta_header_w || ';IE_DIGITO_CONTA_LOTE_P=' || ie_digito_conta_lote_w);
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(280456, 'CD_CONTA_HEADER_P=' || cd_conta_header_w);
end if;

open c01;
loop
fetch c01 into
	ds_dt_deposito_w,
	cd_agencia_recebedora_w,
	ie_digito_agencia_w,
	cd_identificacao_w,
	/*vl_deposito_especie_w,
	vl_deposito_cheque_w,
	vl_deposito_w,*/
	ds_vl_1_w,
	ds_vl_2_w,
	ds_vl_3_w,
	ie_forma_deposito_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	vl_deposito_especie_w	:= (ds_vl_1_w)::numeric;
	vl_deposito_cheque_w	:= (ds_vl_2_w)::numeric;
	vl_deposito_w		:= (ds_vl_3_w)::numeric;

	select	nextval('lote_ret_dep_ident_item_seq')
	into STRICT	nr_sequencia_w
	;

	dt_deposito_w	:= to_date(ds_dt_deposito_w,'DDMMYYYY HH24MISS');

	insert into lote_ret_dep_ident_item(dt_deposito,
				cd_agencia_recebedora,
				ie_digito_agencia,
				cd_identificacao,
				vl_deposito_especie,
				vl_deposito_cheque,
				vl_deposito,
				nm_usuario,
				dt_atualizacao,
				nr_seq_lote,
				ie_forma_deposito,
				nr_sequencia)
			values (dt_deposito_w,
				cd_agencia_recebedora_w,
				ie_digito_agencia_w,
				cd_identificacao_w,
				vl_deposito_especie_w,
				vl_deposito_cheque_w,
				vl_deposito_w,
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_lote_dep_ident_p,
				ie_forma_deposito_w,
				nr_sequencia_w);
	end;

end loop;
close c01;

/* Atualizar data de geração do arquivo */

update	lote_ret_deposito_ident
set	dt_geracao_arquivo	= dt_geracao_arquivo_w,
	dt_importacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_lote_dep_ident_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE import_dep_ident_bradesco_dp06 ( nr_seq_lote_dep_ident_p bigint, nm_usuario_p text) FROM PUBLIC;

