CREATE OR REPLACE PROCEDURE import_evento_cirurgia ( nr_seq_modelo_p bigint, cd_estab_modelo_p bigint ) AS $body$
DECLARE


nr_execucao_max_w	    integer;

dt_atualizacao_w		timestamp;
nm_usuario_w		  	varchar(15);
dt_atualizacao_nrec_w 	timestamp;
nm_usuario_nrec_w	  	varchar(15);
nr_ordem_exibicao_w		integer;
nr_seq_evento_w			bigint;

c_evento_cirurgia CURSOR FOR
SELECT  dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_execucao,
		nr_sequencia
from    evento_cirurgia
where   coalesce(ie_situacao, 'A')  = 'A'
and		cd_estabelecimento = cd_estab_modelo_p
and     nr_sequencia not in ( select nr_seq_evento from pepo_modelo_evento where nr_seq_modelo = nr_seq_modelo_p )
order by nr_seq_execucao;

BEGIN

	select  coalesce(max(nr_ordem_exibicao),0)
	into STRICT	nr_execucao_max_w
	from    PEPO_MODELO_EVENTO a
	where   nr_seq_modelo = nr_seq_modelo_p;
	
	<<read_evento_cirurgia>>
	for r_evento_cirurgia in c_evento_cirurgia
	 loop
		
		nr_execucao_max_w := nr_execucao_max_w + 1;
		
		insert	into pepo_modelo_evento(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_modelo,
			nr_ordem_exibicao,
			nr_seq_evento)
		values (nextval('pepo_modelo_evento_seq'),
			r_evento_cirurgia.dt_atualizacao,
			r_evento_cirurgia.nm_usuario,
			r_evento_cirurgia.dt_atualizacao_nrec,
			r_evento_cirurgia.nm_usuario_nrec,
			nr_seq_modelo_p,			
			nr_execucao_max_w,
			r_evento_cirurgia.nr_sequencia
		);
	 end loop read_evento_cirurgia;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
