SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE import_itens_ag_int_opmenexo ( id_pedido_p text, nm_usuario_p text, cd_material_p text, qt_material_p bigint, vl_unitario_p text, vl_total_p text, ds_observacao_p text, ds_marca_p text, cd_cnpj_p text, ds_razao_social_p text, ie_opcao_p text, prazo_entrega_p bigint, nm_fantasia_p text, ds_forma_pagamento_p text) AS $body$
DECLARE

			
			
cd_cnpj_w		varchar(14);
nr_sequencia_w		agenda_paciente.nr_sequencia%type;
cd_cond_pagto_w		bigint;
ds_erro_w		varchar(255);
nr_seq_agenda_w		bigint;
qt_existe_w		bigint;
nr_cot_compra_w		bigint;
cd_moeda_w		integer;
cd_condicao_pagto_w	bigint;
nr_seq_cot_fornecedor_w	bigint;
cd_material_w		material.cd_material%type;
ie_cadastra_pj_w		varchar(1) := 'S';
cd_tipo_pessoa_w		smallint;
cd_estab_w		smallint;
ds_forma_pagto_fornec_w	varchar(80);
ds_titulo_w		varchar(255);
ds_comunicado_w		varchar(2000);
nr_seq_agenda_pac_w	bigint;
nr_seq_fornec_w		bigint;
nr_item_cotacao_w		integer;
nr_sequencia_item_w	bigint;
nr_item_cot_compra_w	integer;
cd_estabelecimento_w	smallint;
cd_cep_w		varchar(15);
sg_estado_w		pessoa_juridica.sg_estado%type;
nr_seq_classif_w		bigint;
dt_retorno_prev_w		timestamp;
qt_existe_pac_opme_w	bigint;
nr_seq_pac_opme_ger_w	bigint;
ie_grava_cotacao_w	parametros_opme.ie_grava_cotacao%type;
ie_atualiza_tabela_w	parametros_opme.ie_atualiza_tabela%type;
cd_convenio_w		convenio.cd_convenio%type;
cd_categoria_w		categoria_convenio.cd_categoria%type;
dt_agenda_w		timestamp;
cd_tabela_preco_w	tabela_preco_material.cd_tab_preco_mat%type;
cd_tabela_preco_ww	tabela_preco_material.cd_tab_preco_mat%type;
vl_unitario_w		double precision;
vl_total_w		double precision;
ie_copiar_preco_estab_w parametros_opme.ie_copiar_preco_estab%type;
cd_perfil_opme_w	bigint;
cd_perfil_opme_ww	bigint;
ds_modulo_w		varchar(48);
ds_action_w		varchar(32) := '';
ds_action_old_w		varchar(32) := '';

c02 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	ie_situacao = 'A';


BEGIN

begin
	vl_unitario_w 	:= (REPLACE(vl_unitario_p, '.', ''))::numeric;
exception when others then
	vl_unitario_w := (REPLACE(REPLACE(vl_unitario_p, '.', ''), ',', '.'))::numeric;
end;

begin
	vl_total_w 	:= (REPLACE(vl_total_p, '.', ''))::numeric;
exception when others then
	vl_total_w := (REPLACE(REPLACE(vl_total_p, '.', ''), ',', '.'))::numeric;
end;

cd_material_w	:= somente_numero(cd_material_p);
cd_cnpj_w	:= cd_cnpj_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	agenda_paciente
where	cd_sistema_externo = id_pedido_p;

select	max(cd_estabelecimento),
	max(b.cd_convenio),
	max(b.cd_categoria),
	max(b.dt_agenda)
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	cd_categoria_w,
	dt_agenda_w
from	agenda a,
	agenda_paciente b
where 	a.cd_agenda = b.cd_agenda
and	b.nr_sequencia = nr_sequencia_w;

select	coalesce(max(ie_copiar_preco_estab), 'N')
into STRICT 	ie_copiar_preco_estab_w
from	parametros_opme
where 	cd_estabelecimento = cd_estabelecimento_w
and	ie_sistema_integracao = 'P';

if (ie_opcao_p = 1) then
	
	select	count(*)
	into STRICT	qt_existe_pac_opme_w
	from 	agenda_pac_opme
	where	nr_seq_agenda = nr_sequencia_w
	and 	cd_material = cd_material_w
	and 	coalesce(cd_cgc, cd_cnpj_w) = cd_cnpj_w
	and     ie_origem_inf <> 'E'; /* Alteracao OS 1815358 - Desconsiderar itens excluidos */
	
	select	coalesce(max(ie_atualiza_tabela), 'N'),
		max(cd_tabela_preco),
		coalesce(max(cd_perfil_opme), 0)
	into STRICT	ie_atualiza_tabela_w,
		cd_tabela_preco_w,
		cd_perfil_opme_w
	from	parametros_opme
	where	ie_sistema_integracao = 'P'
	and	cd_estabelecimento = cd_estabelecimento_w;
	
	if (coalesce(vl_unitario_w,0) > 0) then
		if (ie_atualiza_tabela_w = 'S') and (cd_tabela_preco_w IS NOT NULL AND cd_tabela_preco_w::text <> '') then
			CALL atualiza_tab_preco_OPMENexo(cd_material_w, clock_timestamp(), cd_tabela_preco_w, cd_estabelecimento_w, vl_unitario_w, cd_cnpj_w, nm_usuario_p);
			if (ie_copiar_preco_estab_w = 'S') then
				CALL copiar_tabela_preco_mat_estab(cd_tabela_preco_w, cd_estabelecimento_w, cd_material_w, trunc(clock_timestamp()), nm_usuario_p);
			end if;
		elsif (ie_atualiza_tabela_w in ('C', 'D')) then
			
			select 	max(cd_tab_preco_mat)
			into STRICT	cd_tabela_preco_ww
			from	convenio_preco_mat
			where 	cd_convenio = cd_convenio_w
			and	cd_categoria = cd_categoria_w
			and	ie_integracao_opme = 'S'
			and 	cd_estabelecimento = cd_estabelecimento_w;
			
			if (coalesce(cd_tabela_preco_ww, coalesce(cd_tabela_preco_w, 0)) > 0) then
				if (ie_atualiza_tabela_w = 'D')  then
					CALL atua_tab_preco_dt_age_opmenexo(cd_material_w, dt_agenda_w, coalesce(cd_tabela_preco_ww, cd_tabela_preco_w), cd_estabelecimento_w, vl_unitario_w, cd_cnpj_w, nm_usuario_p);
					
					if (ie_copiar_preco_estab_w = 'S') then
						CALL copiar_tabela_preco_mat_estab(coalesce(cd_tabela_preco_ww, cd_tabela_preco_w), cd_estabelecimento_w, cd_material_w, trunc(dt_agenda_w), nm_usuario_p);
					end if;
				else
					CALL atua_tab_preco_dt_age_opmenexo(cd_material_w, clock_timestamp(), coalesce(cd_tabela_preco_ww, cd_tabela_preco_w), cd_estabelecimento_w, vl_unitario_w, cd_cnpj_w, nm_usuario_p);
					
					if (ie_copiar_preco_estab_w = 'S') then
						CALL copiar_tabela_preco_mat_estab(coalesce(cd_tabela_preco_ww, cd_tabela_preco_w), cd_estabelecimento_w, cd_material_w, trunc(clock_timestamp()), nm_usuario_p);
					end if;
				end if;
			end if;
		end if;
	end if;
	
	if (coalesce(qt_existe_pac_opme_w,0) > 0) then
		
		select	max(nr_sequencia)
		into STRICT	nr_seq_pac_opme_ger_w
		from 	agenda_pac_opme
		where	nr_seq_agenda = nr_sequencia_w
		and 	cd_material = cd_material_w
		and 	coalesce(cd_cgc, cd_cnpj_w) = cd_cnpj_w;
		
		update	agenda_pac_opme
		set 	qt_material = qt_material_p,
			vl_unitario_item = vl_unitario_w,
			cd_cgc = cd_cnpj_w,
			ie_integracao = 'S',
			/* OS 1830315 */

			vl_unitario_atualizado = vl_unitario_w,
			dt_atualizacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia = nr_seq_pac_opme_ger_w
		and	cd_material = cd_material_w;
		
	elsif (coalesce(qt_existe_pac_opme_w,0) = 0) then
		
		select	nextval('agenda_pac_opme_seq')
		into STRICT	nr_seq_agenda_pac_w
		;

		insert into agenda_pac_opme(
			nr_sequencia,			nr_seq_agenda,
			dt_atualizacao,			nm_usuario,
			cd_material,			qt_material,
			vl_unitario_item,		vl_desconto,
			cd_cond_pagto,
			ie_origem_inf,			nr_seq_apres,
			ie_padrao,			ds_observacao,
			ie_autorizado,			cd_cgc,
			dt_atualizacao_nrec,		nm_usuario_nrec,
			ie_integracao,			ie_gerar_autor,
			nr_seq_marca,			vl_unitario_atualizado)
		values ( nr_seq_agenda_pac_w,		nr_sequencia_w,
			clock_timestamp(),			nm_usuario_p,
			cd_material_w,			replace(qt_material_p,'.',','),
			vl_unitario_w,			'',
			null,
			'I',				500,
			'S',				ds_observacao_p,
			'P',				cd_cnpj_w,
			clock_timestamp(),			nm_usuario_p,
			'S',				'',
			substr(obter_marca_material(cd_material_w,'C'),1,20),
			vl_unitario_w);
	end if;
	
	/* Verifica e remove dos itens sem codigo os itens que agora ja devem existir na agenda_pac_desc_opme */

		
	select count(*)
	into STRICT qt_existe_w
	from agenda_pac_desc_material
	where  nr_seq_agenda = nr_sequencia_w
	and ie_tipo_item = 2
	and (Elimina_Acentuacao(ds_material) = (SELECT Elimina_Acentuacao(ds_material) from material where cd_material = cd_material_w)
		or Elimina_Acentuacao(ds_material) = (select Elimina_Acentuacao(ds_reduzida) from material where cd_material = cd_material_w)
	);
	
	if (coalesce(qt_existe_w,0) > 0) then
	
	delete from agenda_pac_desc_material
	where  nr_seq_agenda = nr_sequencia_w
	and ie_tipo_item = 2
	and (Elimina_Acentuacao(ds_material) = (SELECT Elimina_Acentuacao(ds_material) from material where cd_material = cd_material_w)
		or Elimina_Acentuacao(ds_material) = (select Elimina_Acentuacao(ds_reduzida) from material where cd_material = cd_material_w)
	);
		
	end if;
	
	
	cd_perfil_opme_ww := wheb_usuario_pck.get_cd_perfil();
	begin	
		CALL wheb_usuario_pck.set_cd_perfil(cd_perfil_opme_w);
		if (cd_perfil_opme_w > 0) then
			DBMS_APPLICATION_INFO.READ_MODULE(ds_modulo_w, ds_action_old_w);
			DBMS_APPLICATION_INFO.SET_ACTION('OPMENEXO');
		else
			DBMS_APPLICATION_INFO.READ_MODULE(ds_modulo_w, ds_action_w);
			if (ds_action_w = 'OPMENEXO') then
				DBMS_APPLICATION_INFO.SET_ACTION('');
			end if;
		end if;
		CALL gerar_autor_regra(null,null,null,null,null,null,'AP',nm_usuario_p,nr_sequencia_w,null,null,null,nr_seq_agenda_pac_w,null,'','','');
		CALL wheb_usuario_pck.set_cd_perfil(cd_perfil_opme_ww);
		
		if (ds_action_old_w IS NOT NULL AND ds_action_old_w::text <> '') then
			DBMS_APPLICATION_INFO.SET_ACTION(ds_action_old_w);
		end if;
		
	exception when others then
		CALL wheb_usuario_pck.set_cd_perfil(cd_perfil_opme_ww);
		CALL wheb_mensagem_pck.exibir_mensagem_abort(substr(sqlerrm(SQLSTATE),1,2000));
	end;
	
	update	material_autorizado a
	set	a.ie_origem_preco	= 8,
		a.dt_atualizacao 	= clock_timestamp(),
		a.nm_usuario	= nm_usuario_p
	where	a.nr_sequencia_autor	in (SELECT	b.nr_sequencia
					from autorizacao_convenio b
					where	b.nr_seq_agenda	= nr_sequencia_w)		
	and	a.nr_seq_opme		= nr_seq_agenda_pac_w
	and	a.cd_material		= cd_material_w
	and	a.cd_cgc_fabricante	= cd_cnpj_w;
	commit;
end if;
	
if (nr_sequencia_w > 0) then

	select	coalesce(max(nr_cot_compra),0)
	into STRICT	nr_cot_compra_w
	from	cot_compra
	where	nr_seq_agenda_pac = nr_sequencia_w;

	if (nr_cot_compra_w > 0) then
		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from	cot_compra
		where	nr_cot_compra	= nr_cot_compra_w;

		select	cd_moeda_padrao,
			cd_condicao_pagamento_padrao,
			coalesce(cd_tipo_pessoa_bionexo,0)
		into STRICT	cd_moeda_w,
			cd_condicao_pagto_w,
			cd_tipo_pessoa_w
		from	parametro_compras
		where	cd_estabelecimento	= cd_estabelecimento_w;
		
		select	count(*)
		into STRICT	qt_existe_w
		from	pessoa_juridica
		where	cd_cgc		= cd_cnpj_w;

		if (ie_cadastra_pj_w = 'S') and (qt_existe_w = 0) then	

			if (cd_tipo_pessoa_w = 0) then	
				select	min(cd_tipo_pessoa)
				into STRICT	cd_tipo_pessoa_w
				from	pessoa_juridica
				where	ie_situacao = 'A';
			end if;
		
			select	coalesce(cd_cep,0),
				coalesce(sg_estado,'SP')
			into STRICT	cd_cep_w,
				sg_estado_w
			from	pessoa_juridica
			where	cd_cgc = obter_cgc_estabelecimento(cd_estabelecimento_w);
			
			insert into pessoa_juridica(
				cd_cgc,
				ds_razao_social,
				nm_fantasia,
				cd_cep,
				ds_endereco,
				ds_municipio,
				sg_estado,
				dt_atualizacao,
				nm_usuario,
				cd_tipo_pessoa,
				ie_prod_fabric,
				ie_situacao,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			values (	cd_cnpj_w,
				substr(ds_razao_social_p,1,255),
				substr(nm_fantasia_p,1,255),
				cd_cep_w,
				wheb_mensagem_pck.get_texto(802243),
				wheb_mensagem_pck.get_texto(802243),
				sg_estado_w,
				clock_timestamp(),
				nm_usuario_p,
				cd_tipo_pessoa_w,
				'N',
				'A',
				clock_timestamp(),
				nm_usuario_p);
				
			open C02;
			loop
			fetch C02 into	
				cd_estab_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin
				
				insert into pessoa_juridica_estab(
					nr_sequencia,
					cd_cgc,
					cd_estabelecimento,
					dt_atualizacao,
					nm_usuario,
					ie_conta_dif_nf,
					ie_rateio_adiant,
					ie_retem_iss)
				values (	nextval('pessoa_juridica_estab_seq'),
					cd_cnpj_w,
					cd_estab_w,
					clock_timestamp(),
					nm_usuario_p,
					'N',
					'P',
					'N');	
				end;
			end loop;
			close C02;
		
			select	obter_classif_comunic('F')
			into STRICT	nr_seq_classif_w
			;

			ds_titulo_w	:= 	WHEB_MENSAGEM_PCK.get_texto(302769);
			ds_comunicado_w	:= 	WHEB_MENSAGEM_PCK.get_texto(302770,'NR_COT_COMPRA_W=' || nr_cot_compra_w || ';' || 'CD_CNPJ_W=' || cd_cnpj_w || ';' ||
										'DS_RAZAO_SOCIAL_P=' || substr(ds_razao_social_p,1,80));
		
			insert	into comunic_interna(
				dt_comunicado,		ds_titulo,			ds_comunicado,
				nm_usuario,		dt_atualizacao,			ie_geral,
				nm_usuario_destino,	nr_sequencia,			ie_gerencial,
				nr_seq_classif,		dt_liberacao)
			values (	clock_timestamp(),		ds_titulo_w,			ds_comunicado_w,
				nm_usuario_p,		clock_timestamp(),			'N',
				nm_usuario_p,		nextval('comunic_interna_seq'),	'N',
				nr_seq_classif_w,	clock_timestamp());
		end if;	

		if (cd_cnpj_w <> '0') and (ie_opcao_p = 0) then

			ds_forma_pagto_fornec_w := '';
			
			select	max(cd_interno)
			into STRICT	ds_forma_pagto_fornec_w
			from	conversao_meio_externo
			where	upper(nm_tabela) = 'CONDICAO_PAGAMENTO'
			and	upper(nm_atributo) = 'CD_CONDICAO_PAGAMENTO'
			and	ie_sistema_externo = 'B'
			and	cd_externo = ds_forma_pagamento_p;
		
			select	coalesce(max(cd_condicao_pagamento),cd_condicao_pagto_w)
			into STRICT	cd_condicao_pagto_w
			from	condicao_pagamento
			where	upper(cd_condicao_pagamento) = upper(coalesce(ds_forma_pagto_fornec_w,ds_forma_pagamento_p));			
			
			select	nextval('cot_compra_forn_seq')
			into STRICT	nr_seq_cot_fornecedor_w
			;

			insert	into cot_compra_forn(
				nr_sequencia,
				nr_cot_compra,
				cd_cgc_fornecedor,
				dt_atualizacao,
				nm_usuario,
				ds_observacao,
				cd_moeda,
				cd_condicao_pagamento,
				ie_frete,
				ie_gerado_bionexo,
				ie_status_envio_email_lib)
			values (	nr_seq_cot_fornecedor_w,
				nr_cot_compra_w,
				cd_cnpj_w,
				clock_timestamp(),
				nm_usuario_p,
				ds_forma_pagamento_p,
				cd_moeda_w, 
				cd_condicao_pagto_w,
				'C',
				'X',
				'N');
		end if;
		
		if (cd_cnpj_w <> '0') and (ie_opcao_p = 1) then
			
			select	dt_retorno_prev + coalesce(prazo_entrega_p,0)
			into STRICT	dt_retorno_prev_w
			from	cot_compra
			where	nr_cot_compra = nr_cot_compra_w;
			
			select	coalesce(max(nr_item_cot_compra),0) +1
			into STRICT	nr_item_cot_compra_w
			from	cot_compra_item
			where	nr_cot_Compra = nr_cot_Compra_w;
			
			insert into cot_compra_item(		
				nr_cot_compra,
				nr_item_cot_compra,
				cd_material,
				qt_material,
				cd_unidade_medida_compra,
				dt_atualizacao,
				dt_limite_entrega,
				nm_usuario,
				ie_situacao)
			values (	nr_cot_compra_w,
				nr_item_cot_compra_w,
				cd_material_w,
				replace(qt_material_p,'.',','),
				obter_dados_material(cd_material_w,'UMP'),
				clock_timestamp(),
				dt_retorno_prev_w,
				nm_usuario_p,
				'A');
				
			insert into cot_compra_item_entrega(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_cot_compra,
				nr_item_cot_compra,
				dt_entrega,
				qt_entrega)
			values (	nextval('cot_compra_item_entrega_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_cot_compra_w,
				nr_item_cot_compra_w,
				dt_retorno_prev_w,
				replace(qt_material_p,'.',','));	
		
			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_fornec_w
			from	cot_compra_forn
			where	nr_cot_compra = nr_cot_compra_w
			and	cd_cgc_fornecedor = cd_cnpj_w;
			
			if (nr_seq_fornec_w > 0) then
			
				select	coalesce(min(a.nr_item_cot_compra),0)
				into STRICT	nr_item_cotacao_w
				from	cot_compra_item a
				where	a.nr_cot_compra	= nr_cot_compra_w
				and	a.cd_material = cd_material_w;		
			
				if (nr_item_cotacao_w = 0) then
					select	coalesce(min(a.nr_item_cot_compra),0)
					into STRICT	nr_item_cotacao_w
					from	cot_compra_item a,
						cot_compra_item_aceite b
					where	a.nr_cot_compra = b.nr_cot_compra
					and	a.nr_item_cot_compra = b.nr_item_cot_compra
					and	a.nr_cot_compra	= nr_cot_compra_w
					and	b.cd_material = cd_material_w;
				end if;
			
				update	cot_compra_item
				set	ie_situacao = 'A'
				where	nr_cot_compra = nr_cot_compra_w
				and	nr_item_cot_compra = nr_item_cotacao_w
				and	coalesce(ie_situacao,'A') <> 'A';

			
				select	nextval('cot_compra_forn_item_seq')
				into STRICT	nr_sequencia_item_w
				;

				insert into cot_compra_forn_item(
					nr_sequencia,
					nr_seq_cot_forn,
					nr_cot_compra,
					nr_item_cot_compra,
					cd_cgc_fornecedor,
					qt_material,
					vl_unitario_material,
					dt_atualizacao,
					nm_usuario,
					vl_preco_liquido,
					vl_total_liquido_item,
					ie_situacao,
					ds_marca,
					ds_marca_fornec,
					cd_material)
				values (	nr_sequencia_item_w,
					nr_seq_fornec_w,
					nr_cot_compra_w,
					nr_item_cotacao_w,
					cd_cnpj_w,
					replace(qt_material_p,'.',','),
					replace(vl_unitario_w,'.',','),
					clock_timestamp(),
					nm_usuario_p,
					replace(vl_total_w,'.',','),
					replace(vl_total_w,'.',','),
					'A',
					substr(ds_marca_p,1,30),
					substr(ds_marca_p,1,30),
					cd_material_w);
			end if;
		end if;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
