CREATE OR REPLACE PROCEDURE import_pessoa_fisica_txt ( cd_pessoa_fisica_externo_p text, cd_usuario_convenio_p text, nm_pessoa_fisica_p text, ie_estado_civil_p text, nr_cpf_p text, nr_identidade_p text, ds_endereco_p text, ds_bairro_p text, ds_municipio_p text, sg_estado_p text, cd_cep_p text, ie_dependencia_sus_p text, ie_sexo_p text, dt_nascimento_p text, cd_complemento_p text, nm_usuario_p text) AS $body$
DECLARE


		
cd_pessoa_fisica_w	varchar(10);
nr_sequencia_w		smallint;
nr_cpf_w			varchar(11);
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type;
			

BEGIN

select wheb_usuario_pck.get_cd_estabelecimento
into STRICT cd_estabelecimento_w
;

select	nextval('pessoa_fisica_seq')
into STRICT	cd_pessoa_fisica_w
;
	
	
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '')	then
	
	nr_cpf_w := substr(elimina_caracteres_especiais(nr_cpf_p),1,14);		

	insert into pessoa_fisica(
			cd_pessoa_fisica,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_tipo_pessoa,
			nm_usuario,
			nm_pessoa_fisica,
			ie_estado_civil,
			nr_cpf,
			nr_identidade,
			ie_dependencia_sus,
			ie_sexo,
			dt_nascimento)
		values (	cd_pessoa_fisica_w,	
				clock_timestamp(),
				clock_timestamp(),
				'2',
				nm_usuario_p,
				nm_pessoa_fisica_p,
				ie_estado_civil_p,
				nr_cpf_w,
				nr_identidade_p,
				ie_dependencia_sus_p,
				ie_sexo_p,
				dt_nascimento_p);	
		
			
	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;
			
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		
		insert into compl_pessoa_fisica(
				cd_pessoa_fisica,
				dt_atualizacao,
				dt_atualizacao_nrec,
				ie_tipo_complemento,
				nm_usuario,
				nr_sequencia,
				ds_endereco,
				ds_bairro,
				ds_municipio,
				sg_estado,
				cd_cep )
			values ( cd_pessoa_fisica_w,
				clock_timestamp(),
				clock_timestamp(),
				'1',
				nm_usuario_p,
				nr_sequencia_w,
				ds_endereco_p,
				ds_bairro_p,
				ds_municipio_p,
				sg_estado_p,
				cd_cep_p);
	end if;
		
		
		insert into pf_codigo_externo(	
				nr_sequencia,
				cd_pessoa_fisica,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				cd_pessoa_fisica_externo,
				cd_usuario_convenio,
				cd_complemento,
				cd_estabelecimento)
			values (nextval('pf_codigo_externo_seq'),	
				cd_pessoa_fisica_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_externo_p,
				cd_usuario_convenio_p,
				cd_complemento_p,
				cd_estabelecimento_w);
end if;	
		


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
