CREATE OR REPLACE PROCEDURE import_sus_aih_cid (cd_doenca_cid_p text, ds_doenca_cid_p text, cd_categoria_cid_p text, ie_opcao_p text, ie_restricao_sexo_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Importar dados AIH
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
AT - atualizacao_padrao_parametros
IN - inserir dado tabela cid_especialidade
UP - update cid_categoria
IC - inserir dado tabela cid_categoria
UD - update cid_doenca
ID - inserir dado cid_doenca
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (upper(ie_opcao_p) = 'AT')then
	update	cid_doenca
	set	ie_situacao = 'I',
		nm_usuario = 'Tasy',
		dt_atualizacao = clock_timestamp()
	where	ie_situacao = 'A';

elsif (upper(ie_opcao_p) = 'IN')then
	insert	into cid_especialidade(cd_especialidade_cid,
			ds_especialidade_cid,
			dt_atualizacao,
			nm_usuario,
			ie_situacao)
	values (1,
			substr(wheb_mensagem_pck.get_texto(313654),1,255),
			clock_timestamp(),
			nm_usuario_p,
			'A');

elsif (upper(ie_opcao_p) = 'UP')then
	update	cid_categoria
	set	ds_categoria_cid = ds_doenca_cid_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	cd_categoria_cid = cd_doenca_cid_p;

elsif (upper(ie_opcao_p) = 'IC') then
	insert	into	cid_categoria(cd_categoria_cid,
		ds_categoria_cid,
		dt_atualizacao,
		nm_usuario,
		cd_especialidade,
		ie_situacao)
	values (cd_doenca_cid_p,
		ds_doenca_cid_p,
		clock_timestamp(),
		nm_usuario_p,
		1,
		'A');

elsif (upper(ie_opcao_p) = 'UD') then
	update	cid_doenca
	set	ds_doenca_cid = ds_doenca_cid_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		cd_categoria_cid = substr(cd_doenca_cid_p,1,3),
		ie_sexo = ie_restricao_sexo_p,
		ie_cad_interno = 'N',
		ie_situacao = 'A'
	where	cd_doenca_cid = cd_doenca_cid_p;

elsif (upper(ie_opcao_p) = 'ID') then
	insert	into cid_doenca(cd_doenca_cid,
			ds_doenca_cid,
			dt_atualizacao,
			nm_usuario,
			cd_categoria_cid,
			ie_sexo,
			ie_cad_interno,
			ie_situacao)
	values (cd_doenca_cid_p,
			ds_doenca_cid_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_categoria_cid_p,
			ie_restricao_sexo_p,
			'N',
			'A');
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
