CREATE OR REPLACE PROCEDURE import_sus_aih_detalhe (cd_detalhe_p text, ds_detalhe_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Importar dados AIH
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
IN - insere dado tabela sus_detalhe
UP - update
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (upper(ie_opcao_p) = 'IN') then
	if (cd_detalhe_p IS NOT NULL AND cd_detalhe_p::text <> '') and (ds_detalhe_p IS NOT NULL AND ds_detalhe_p::text <> '') then
			insert	into sus_detalhe(nr_sequencia,
					cd_detalhe,
					ds_detalhe,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
			values (nextval('sus_detalhe_seq'),
					cd_detalhe_p,
					substr(ds_detalhe_p,1,100),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p);
	end if;
elsif (upper(ie_opcao_p) = 'UP') then
	update	sus_detalhe
	set	ds_detalhe = substr(ds_detalhe_p,1,100),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	lpad(cd_detalhe,5,0) = lpad(cd_detalhe_p,5,0);
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
