CREATE OR REPLACE PROCEDURE import_sus_aih_mtv_cobr_unif (cd_motivo_cobranca_p bigint, ds_motivo_cobranca_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Importar dados AIH para a tabela sus_motivo_cobr_unif
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
AT - atualizacao_padrao_parametros
IN - inserir dado
UP - update
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
if (upper(ie_opcao_p) = 'AT')then
	update	sus_motivo_cobr_unif
	set	ie_situacao = 'I';
	commit;
else
	if (cd_motivo_cobranca_p IS NOT NULL AND cd_motivo_cobranca_p::text <> '') and (ds_motivo_cobranca_p IS NOT NULL AND ds_motivo_cobranca_p::text <> '') then

		if (upper(ie_opcao_p) = 'IN') then
			insert	into sus_motivo_cobr_unif(cd_motivo_cobranca,
					ds_motivo_cobranca,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					ie_situacao)
			values (cd_motivo_cobranca_p,
					ds_motivo_cobranca_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					'A');
		elsif (upper(ie_opcao_p) = 'UP') then
			update	sus_motivo_cobr_unif
			set	ds_motivo_cobranca = ds_motivo_cobranca_p,
				ie_situacao = 'A'
			where	cd_motivo_cobranca = cd_motivo_cobranca_p;
		end if;
	end if;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
