CREATE OR REPLACE PROCEDURE import_sus_aih_preco (vl_sa_p bigint, vl_sh_p bigint, vl_sp_p bigint, qt_pontos_ato_p bigint, dt_vigencia_inicial_p timestamp, dt_vigencia_final_p timestamp, vl_total_hospitalar_p bigint, ie_versao_aih_p text, cd_procedimento_p bigint, dt_comp_aih_p timestamp, ie_novo_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Importar dados AIH para a tabela sus_preco
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
