BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (vl_total_hospitalar_p IS NOT NULL AND vl_total_hospitalar_p::text <> '') and (vl_sp_p IS NOT NULL AND vl_sp_p::text <> '') and (vl_sh_p IS NOT NULL AND vl_sh_p::text <> '') and (vl_sa_p IS NOT NULL AND vl_sa_p::text <> '') then
		if (upper(ie_novo_p) = 'S')then
			insert into sus_preco( vl_sadt,
						vl_sa,
						vl_sh,
						vl_sp,
						qt_pontos_ato,
						dt_vigencia_inicial,
						dt_vigencia_final,
						vl_total_hospitalar,
						dt_atualizacao,
						nm_usuario,
