						cd_procedimento,
						ie_origem_proced,
						vl_total_amb,
						dt_competencia,
						ie_aih)
			 values (0,
						vl_sa_p,
						vl_sh_p,
						vl_sp_p,
						qt_pontos_ato_p,
						dt_vigencia_inicial_p,
						dt_vigencia_final_p,
