SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE import_sus_aih_proc_cbo (cd_procedimento_p bigint, cd_cbo_p text, ie_opcao_p text, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Importar dados AIH
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ie_opcao_p
DE - atualizacao_padrao_parametros(delete)
IN - inserir dado tabela sus_procedimento_cbo
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_sequencia_w	sus_procedimento_cbo.nr_sequencia%type;


BEGIN

if (upper(ie_opcao_p) = 'DE')then

	delete	from sus_procedimento_cbo
	where	Obter_Situacao_Procedimento(cd_procedimento,ie_origem_proced) = 'A'
	and	((ie_aih = 'S') or (coalesce(ie_aih::text, '') = '' and coalesce(ie_bpa::text, '') = ''));

elsif (upper(ie_opcao_p) = 'IN') and (cd_cbo_p IS NOT NULL AND cd_cbo_p::text <> '') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then

		select	nextval('sus_procedimento_cbo_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into sus_procedimento_cbo(cd_procedimento,
				ie_origem_proced,
				cd_cbo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_sequencia,
				ie_aih)
		values (cd_procedimento_p,
				7,
				cd_cbo_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_sequencia_w,
				'S');

end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
