nr_sequencia_w	w_sus_aih_valor.nr_sequencia%type;

BEGIN
if (upper(ie_opcao_p) = 'DL') then
	delete	from sus_proc_compativel
	where	((Obter_Situacao_Procedimento(cd_proc_principal,ie_origem_proc_princ) = 'A') or (Obter_Situacao_Procedimento(cd_proc_secundario,ie_origem_proc_sec) = 'A'));

elsif (upper(ie_opcao_p) = 'IN') then
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_proc_principal_p IS NOT NULL AND cd_proc_principal_p::text <> '') and (cd_proc_secundario_p IS NOT NULL AND cd_proc_secundario_p::text <> '') and (cd_reg_proc_sec_p IS NOT NULL AND cd_reg_proc_sec_p::text <> '') and (ie_tipo_compatibilidade_p IS NOT NULL AND ie_tipo_compatibilidade_p::text <> '') then
			insert	into sus_proc_compativel( nr_sequencia,
					cd_proc_principal,
					ie_origem_proc_princ,
					cd_reg_proc_princ,
					cd_proc_secundario,
					ie_origem_proc_sec,
					cd_reg_proc_sec,
					qt_proc_permitida,
					ie_tipo_compatibilidade,
					dt_atualizacao,
