CREATE OR REPLACE PROCEDURE import_watchlist (nr_sequencia_p text) AS $body$
DECLARE


v_wl_cursor REFCURSOR;
wl_record      panorama_acomp_watchlist%ROWTYPE;
v_stmt_str      varchar(200);
cd_profissional_w panorama_acomp_watchlist.CD_PROFISSIONAL%TYPE;

BEGIN
v_stmt_str := 'SELECT * FROM panorama_acomp_watchlist WHERE nr_sequencia IN ('||nr_sequencia_p||')';
select cd_pessoa_fisica into STRICT cd_profissional_w from usuario where nm_usuario = wheb_usuario_pck.get_nm_usuario;
OPEN v_wl_cursor FOR EXECUTE v_stmt_str;
LOOP
  FETCH v_wl_cursor INTO wl_record;
      EXIT WHEN NOT FOUND; /* apply on v_wl_cursor */
  INSERT INTO panorama_watchlist(
        CD_PESSOA_FISICA,
        CD_PROFISSIONAL,
        DT_ATUALIZACAO,
        DT_ATUALIZACAO_NREC,
        NM_USUARIO,
        NM_USUARIO_NREC,
        NR_SEQUENCIA) 
  VALUES (wl_record.CD_PESSOA_FISICA,
          cd_profissional_w,
          clock_timestamp(),
          clock_timestamp(),
          wheb_usuario_pck.get_nm_usuario,
          wheb_usuario_pck.get_nm_usuario,
          wl_record.WATCHLIST_ID);
  INSERT INTO panorama_acomp_watchlist(
        NR_SEQUENCIA,
        CD_PESSOA_FISICA,
        CD_PROFISSIONAL,
        CD_PROFISSIONAL_ORI,
        DT_ATUALIZACAO,
        DT_ATUALIZACAO_NREC,
        NM_USUARIO,
        NM_USUARIO_NREC,
        WATCHLIST_ID)
  VALUES (nextval('panorama_watchlist_seq'),
         wl_record.CD_PESSOA_FISICA,
         cd_profissional_w,
         wl_record.CD_PROFISSIONAL,
         clock_timestamp(),
         clock_timestamp(),
         wheb_usuario_pck.get_nm_usuario,
         wheb_usuario_pck.get_nm_usuario,
         wl_record.WATCHLIST_ID);
  commit;
END LOOP;
CLOSE v_wl_cursor;
end;
$body$
