SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE imp_funcao_macro_inicial ( ds_locale_p text, nm_usuario_p text, cd_funcao_p bigint) AS $body$
DECLARE

			
nr_macro_import_w		bigint;
nr_seq_macro_w		bigint;
ds_macro_w		varchar(255);
ds_locale_w		varchar(10);	

C01 CURSOR FOR
SELECT	b.nr_seq_macro,
	b.ds_macro,
	b.ds_locale,
	b.nr_sequencia
from  	funcao_macro a,
	funcao_macro_inicial b
where	a.nr_sequencia = b.nr_seq_macro
and	coalesce(a.ie_situacao,'A') = 'A'
and	upper(b.ds_locale) = upper(ds_locale_p)
and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
and	coalesce(b.dt_inativacao::text, '') = ''
and not exists (SELECT 1 from funcao_macro_cliente c where c.nr_seq_macro = b.nr_seq_macro and upper(b.ds_locale) = upper(c.ds_locale))
and (coalesce(cd_funcao_p::text, '') = '' or a.cd_funcao = cd_funcao_p);


BEGIN
	
open C01;
loop
fetch C01 into
	nr_seq_macro_w,
	ds_macro_w,	
	ds_locale_w,
	nr_macro_import_w;
	
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	insert	into funcao_macro_cliente(
		nr_sequencia,
		ds_macro,
		nr_seq_macro,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_liberacao,
		ds_locale,
		nr_macro_import)
		values (
		nextval('funcao_macro_cliente_seq'),
		ds_macro_w,
		nr_seq_macro_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		ds_locale_w,
		nr_macro_import_w);			
	
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
