CREATE OR REPLACE PROCEDURE inativar_alerta_atend_alta ( nr_atendimento_p bigint, nm_usuario_p text, nr_seq_tipo_alerta_p bigint, cd_motivo_alta_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT  nr_sequencia
	from	atendimento_alerta
	where 	nr_seq_tipo_alerta = nr_seq_tipo_alerta_p
	and	nr_atendimento     = nr_atendimento_p;




BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_atendimento_p > 0) then
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update 	atendimento_alerta
		set	ie_situacao = 'I',
			dt_inativacao = clock_timestamp(),
			nm_usuario_inativacao = nm_usuario_p
		where	nr_sequencia = nr_sequencia_w;


		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
		end;
	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
