SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inativar_alerta_carta_comprom ( nr_sequencia_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE


qt_alerta_w		integer;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	count(*)
	into STRICT 	qt_alerta_w
	from 	alerta_paciente
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p
	and		coalesce(dt_inativacao::text, '') = ''
	and		ie_situacao = 'A'
	and 	ds_observacao like '%'|| nr_sequencia_p || '%';
	
	if (qt_alerta_w > 0) then

		update	alerta_paciente
		set		dt_inativacao = clock_timestamp(),
				ie_situacao = 'I',
				dt_atualizacao = clock_timestamp(),
				nm_usuario = wheb_usuario_pck.get_nm_usuario
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p
		and		ds_observacao like '%'|| nr_sequencia_p || '%';

		commit;
	
	end if;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
