CREATE OR REPLACE PROCEDURE inativar_alerta_pac_isolamento ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_alerta_w		bigint;

C01 CURSOR FOR
	SELECT coalesce(nr_sequencia,0)
	from 	atendimento_alerta
	where 	nr_seq_tipo_alerta in (SELECT nr_sequencia
				     from tipo_alerta_atend
				     where ie_isolamento = 'S')
	and coalesce(dt_inativacao::text, '') = ''
	and (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and nr_atendimento = nr_atendimento_p
	order by 1;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_alerta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_alerta_w > 0) then
		CALL Atualizar_alerta_Atendimento(nr_seq_alerta_w,'INA',nm_usuario_p);
	end if;
	end;
end loop;
close C01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
