CREATE OR REPLACE PROCEDURE inativar_ativar_unidade_atend (cd_setor_atendimento_p bigint, cd_unidade_basica_p text, cd_unidade_compl_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

qt_unidade_ocupado_w	integer;


BEGIN

	select	count(*)
	into STRICT	qt_unidade_ocupado_w
	from	unidade_atendimento
	where	cd_setor_atendimento 	= cd_setor_atendimento_p
	and	cd_unidade_basica	= cd_unidade_basica_p
	and	cd_unidade_compl	= cd_unidade_compl_p
	and	ie_status_unidade in ('A','P');

	if (qt_unidade_ocupado_w = 0) then

		if (ie_opcao_p = 'I') then
			update 	unidade_atendimento
			set	ie_situacao 		= 'I',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	cd_setor_atendimento 	= cd_setor_atendimento_p
			and	cd_unidade_basica	= cd_unidade_basica_p
			and	cd_unidade_compl	= cd_unidade_compl_p
			and	ie_situacao = 'A';
		end if;

		if (ie_opcao_p = 'A') then
			update 	unidade_atendimento
			set	ie_situacao 		= 'A',
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	cd_setor_atendimento 	= cd_setor_atendimento_p
			and	cd_unidade_basica	= cd_unidade_basica_p
			and	cd_unidade_compl	= cd_unidade_compl_p
			and	ie_situacao = 'I';
		end if;

	end if;
end;
