CREATE OR REPLACE PROCEDURE inativar_ativar_unidade_temp (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_unidade_ocupado_w	integer;
cd_setor_atendimento_w	integer;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);


C01 CURSOR FOR
	SELECT	a.cd_setor_atendimento,
		a.cd_unidade_basica,
		a.cd_unidade_compl
	from	atend_paciente_unidade a,
		unidade_atendimento b
	where	a.nr_atendimento = nr_atendimento_p
	and	a.cd_setor_atendimento 	= b.cd_setor_atendimento
	and	a.cd_unidade_basica	= b.cd_unidade_basica
	and	a.cd_unidade_compl	= b.cd_unidade_compl
	and	ie_temporario = 'S'
	and	ie_status_unidade = 'L'
	and	ie_situacao = 'A';


BEGIN

open C01;
loop
fetch C01 into
	cd_setor_atendimento_w,
	cd_unidade_basica_w,
	cd_unidade_compl_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update 	unidade_atendimento
	set	ie_situacao 		= 'I',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	cd_setor_atendimento 	= cd_setor_atendimento_w
	and	cd_unidade_basica	= cd_unidade_basica_w
	and	cd_unidade_compl	= cd_unidade_compl_w;


	end;
end loop;
close C01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
