CREATE OR REPLACE PROCEDURE inativar_atividade_sprint_dev ( nr_sequencia_p SPRINT_ATIVIDADE.NR_SEQUENCIA%TYPE, ds_justificativa_p SPRINT_ATIVIDADE.DS_JUSTIFICATIVA%TYPE, nm_usuario_inativacao_p SPRINT_ATIVIDADE.NM_USUARIO%type ) AS $body$
DECLARE

    dt_sysdate_s timestamp := clock_timestamp();


BEGIN

  UPDATE  SPRINT_ATIVIDADE
  SET     DT_INATIVACAO     = dt_sysdate_s,
          DS_JUSTIFICATIVA  = ds_justificativa_p,
          NM_USUARIO_INATIVACAO = nm_usuario_inativacao_p,
          IE_SITUACAO = 'I'
  WHERE   NR_SEQUENCIA      = nr_sequencia_p;
  
END;
