CREATE OR REPLACE PROCEDURE inativar_ciap ( nm_usuario_p text , ie_somente_proc_p text) AS $body$
BEGIN


if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')	then

	if (ie_somente_proc_p = 'S') then

		update	problema_ciap
		set		nm_usuario = nm_usuario_p,
				ie_situacao = 'I'
		where	ie_situacao = 'A'
		and		(substr(cd_ciap,2,5))::numeric  >= 30
		and 	(substr(cd_ciap,2,5))::numeric  < 50;

	else

		update	capitulo_ciap
		set		nm_usuario = nm_usuario_p,
				ie_situacao = 'I'
		where	ie_situacao = 'A';

		update	componente_ciap
		set		nm_usuario = nm_usuario_p,
				ie_situacao = 'I'
		where	ie_situacao = 'A';

		update	problema_ciap
		set		nm_usuario = nm_usuario_p,
				ie_situacao = 'I'
		where	ie_situacao = 'A';

		delete from diagnostico_ciap;

		commit;

	end if;

commit;

end if;

end;
$body$
