CREATE OR REPLACE PROCEDURE inativar_dia_tratamento_onco ( nr_seq_paciente_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_status_agenda_w		varchar(3);
ie_cancelar_agend_w		varchar(1);
ie_status_agenda_quimi_w	varchar(3);
nr_seq_agequi_w			bigint;
nr_seq_atendimento_w		bigint;
nr_seq_motivo_w			bigint;
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;

C01 CURSOR FOR 
	SELECT	nr_seq_atendimento 
	from	paciente_atendimento 
	where	nr_seq_paciente	= nr_seq_paciente_p 
	and	coalesce(dt_cancelamento::text, '') = '';
	
C02 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_status_agenda 
	from	agenda_quimio 
	where	nr_seq_atendimento	= nr_seq_atendimento_w;
	

BEGIN 
 
ie_cancelar_agend_w  := Obter_Param_Usuario(281, 1255, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_cancelar_agend_w );
nr_seq_motivo_w  := Obter_Param_Usuario(281, 1256, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, nr_seq_motivo_w );
 
if (ie_cancelar_agend_w <> 'N') then 
 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_atendimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		open C02;
		loop 
		fetch C02 into 
			nr_seq_agequi_w, 
			ie_status_agenda_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
				if (nr_seq_agequi_w	> 0) and (ie_status_agenda_w	<> 'C') then 
					CALL qt_alterar_status_agenda(nr_seq_agequi_w, 'C', nm_usuario_p, 'N', coalesce(nr_seq_motivo_w,null), null, cd_estabelecimento_w,'');
				end if;
				 
			end;
		end loop;
		close C02;
		end;
	end loop;
	close C01;
end if;
 
end;
$body$
