CREATE OR REPLACE PROCEDURE inativar_emprestimo ( nr_emprestimo_p bigint, ds_observacao_p text) AS $body$
BEGIN

update 	emprestimo
set	ds_observacao = substr(ds_observacao_p,1,255),
	ie_situacao   = 'I'
where	nr_emprestimo = nr_emprestimo_p;

commit;

end;
