CREATE OR REPLACE PROCEDURE inativar_episodio_paciente_drg (nr_seq_episodio_p bigint, nr_seq_propaci_drg_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	conta_paciente.nr_atendimento%type;
			
c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_propaci
from	episodio_paciente_drg
where	nr_seq_episodio_paciente = nr_seq_episodio_p
and	ie_situacao = 'A';

c01_w	c01%rowtype;
	

BEGIN

if (BILLING_I18N_PCK.get_inativar_drg = 'S') then

	select max(nr_atendimento)
	into STRICT   nr_atendimento_w
	from   atendimento_paciente
	where  nr_seq_episodio = nr_seq_episodio_p;

	open C01;
	loop
	fetch C01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		--tratamento para n√£o inativar a DRG, quando ela gera um novo procedimento na conta.
		if (coalesce(c01_w.nr_seq_propaci::text, '') = '') or (coalesce(nr_seq_propaci_drg_p::text, '') = '') or (c01_w.nr_seq_propaci <> nr_seq_propaci_drg_p) then		

			update 	episodio_paciente_drg
			set	ie_situacao 	= 'I'		
			where  	nr_sequencia	= c01_w.nr_sequencia;
			
			if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (c01_w.nr_seq_propaci IS NOT NULL AND c01_w.nr_seq_propaci::text <> '') then
				CALL GERAR_DRG_PROC_PAC_CONTA(nr_atendimento_w, nr_seq_episodio_p, c01_w.nr_sequencia, nr_interno_conta_p, nm_usuario_p);
			end if;
			
		elsif (c01_w.nr_seq_propaci = nr_seq_propaci_drg_p) then
		
			update 	episodio_paciente_drg
			set	ie_situacao 	= 'I'		
			where  	nr_sequencia	= c01_w.nr_sequencia;		
		end if;
		
	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
