SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE inativar_fenotipagem ( nr_sequencia_p bigint, nm_usuario_inativacao_p text ) AS $body$
DECLARE


nr_seq_doacao_w   san_result_fenotipagem.nr_seq_doacao%type;


BEGIN
    IF ( coalesce(nr_sequencia_p, 0) > 0 ) THEN
        UPDATE san_result_fenotipagem
        SET
            nm_usuario_inativacao = nm_usuario_inativacao_p,
            ie_situacao = 'I',
            dt_inativacao = clock_timestamp()
        WHERE
            nr_sequencia = nr_sequencia_p;
	
	select coalesce(max(nr_seq_doacao),	max(nr_seq_producao))
		into STRICT nr_seq_doacao_w
	from san_result_fenotipagem
	where	nr_sequencia	= nr_sequencia_p;

	CALL San_Gerar_Codigo_ISBT.san_gravar_cod_isbt_fenot(nr_seq_doacao_w);

    END IF;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
