CREATE OR REPLACE PROCEDURE inativar_hist_acionamento_sla ( nr_seq_hist_p man_ordem_serv_tecnico.nr_sequencia%type, ds_motivo_inativacao_p text, nm_usuario_p text ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_relat_tecnico_old_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
ds_relat_tecnico_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;

nr_seq_ordem_serv_w		man_ordem_servico.nr_sequencia%type;

nr_seq_idioma_w			pessoa_juridica.nr_seq_idioma%type;


BEGIN
	select	most.ds_relat_tecnico
	into STRICT	ds_relat_tecnico_old_w
	from	man_ordem_serv_tecnico most
	where	most.nr_sequencia = nr_seq_hist_p;
	
	select	most.nr_seq_ordem_serv
	into STRICT	nr_seq_ordem_serv_w
	from	man_ordem_serv_tecnico most
	where	most.nr_sequencia = nr_seq_hist_p;
	
	select	man_obter_idioma_os_local(nr_seq_ordem_serv_w)
	into STRICT	nr_seq_idioma_w
	;
	
	select	substr(obter_desc_exp_idioma(953399, nr_seq_idioma_w)
			|| chr(13) || chr(10)
			|| chr(13) || chr(10)
			|| obter_desc_exp_idioma(953884, nr_seq_idioma_w)
			|| chr(13) || chr(10)
			|| chr(13) || chr(10)
			|| ds_relat_tecnico_old_w, 1, 4000)
	into STRICT	ds_relat_tecnico_w
	;

	update	man_ordem_serv_tecnico
	set	dt_inativacao = clock_timestamp(),
		ds_inativacao = ds_motivo_inativacao_p
	where	nr_sequencia = nr_seq_hist_p;
	
	update	man_ordem_servico
	set	dt_acionamento_sla  = NULL
	where	nr_sequencia = nr_seq_ordem_serv_w;
	
	insert
	into man_ordem_serv_tecnico(
		nr_sequencia,
		nr_seq_tipo,
		nr_seq_ordem_serv,
		ds_relat_tecnico,
		nm_usuario,
		nm_usuario_lib,
		dt_atualizacao,
		dt_liberacao,
		dt_historico
		)
	values (
		nextval('man_ordem_serv_tecnico_seq'),
		1, -- Informações relevantes da OS
		nr_seq_ordem_serv_w,
		ds_relat_tecnico_w,
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp());

	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
