CREATE OR REPLACE PROCEDURE inativar_hist_acionamnento_sla ( nr_seq_hist_p man_ordem_serv_tecnico.nr_sequencia%type, nm_usuario_p text ) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_relat_tecnico_old_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
ds_relat_tecnico_w		man_ordem_serv_tecnico.ds_relat_tecnico%type;
nr_seq_tipo_hist_w		man_ordem_serv_tecnico.nr_seq_tipo%type;
nr_seq_ordem_serv_w		man_ordem_servico.nr_sequencia%type;


BEGIN
	select	most.ds_relat_tecnico
	into STRICT	ds_relat_tecnico_old_w
	from	man_ordem_serv_tecnico most
	where	most.nr_sequencia = nr_seq_hist_p;
	
	select	substr(obter_desc_expressao(953399)
			|| chr(13) || chr(10)
			|| chr(13) || chr(10)
			|| ds_relat_tecnico_old_w, 1, 4000)
	into STRICT	ds_relat_tecnico_w
	;
	
	select	most.nr_seq_ordem_serv
	into STRICT	nr_seq_ordem_serv_w
	from	man_ordem_serv_tecnico most
	where	most.nr_sequencia = nr_seq_hist_p;

	select	msr.nr_seq_tipo_hist
	into STRICT	nr_seq_tipo_hist_w
	from	man_sla ms,
		man_sla_regra msr,
		man_sla_loc msl,
		man_ordem_servico mos
	where	ms.nr_sequencia = msr.nr_seq_sla
	and	ms.nr_sequencia = msl.nr_seq_sla
	and	mos.dt_ordem_servico between ms.dt_vigencia and coalesce(ms.dt_fim_vigencia, clock_timestamp())
	and	msr.ie_classificacao = mos.ie_classificacao
	and	msr.ie_prioridade = mos.ie_prioridade
	and	msl.nr_seq_local = mos.nr_seq_localizacao
	and	(msr.nr_seq_tipo_hist IS NOT NULL AND msr.nr_seq_tipo_hist::text <> '')
	and	mos.nr_sequencia = nr_seq_ordem_serv_w;
	
	update	man_ordem_serv_tecnico
	set	dt_inativacao = clock_timestamp()
	where	nr_sequencia = nr_seq_hist_p;
	
	insert
	into man_ordem_serv_tecnico(
		nr_sequencia,
		nr_seq_tipo,
		nr_seq_ordem_serv,
		ds_relat_tecnico,
		nm_usuario,
		nm_usuario_lib,
		dt_atualizacao,
		dt_liberacao,
		dt_historico
		)
	values (
		nextval('man_ordem_serv_tecnico_seq'),
		nr_seq_tipo_hist_w,
		nr_seq_ordem_serv_w,
		ds_relat_tecnico_w,
		nm_usuario_p,
		nm_usuario_p,
		clock_timestamp(),
		clock_timestamp(),
		clock_timestamp());

	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
